mapper.update:
UPDATE T_Corpus
SET corpus_name = #{corpusName}
WHERE id = #{id}

UPDATE T_Corpus
SET essay_number = essay_number + 1
WHERE id = #{id}

UPDATE T_Corpus
SET essay_word_number = essay_word_number + #{difference}
WHERE id = #{id}

UPDATE T_Corpus
SET essay_word_number = essay_word_number - #{difference}
WHERE id = #{id} and essay_word_number > 0

UPDATE T_Corpus
SET user_focus_number = user_focus_number + 1
WHERE id = #{id}
